package variaveis;

public class EstruturaRepeticaoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 1;
		int b = 10;
		
		while(a <= 10) {
			System.out.println("O numero atual é a: "+ a);
			a++;
			
		}
		System.out.println("-----------------------------");
		
		do{
			System.out.println("O numero atual é b: "+ b);
			b--;
		}while(b >= 1); 
	}
}
	

	

